import React, { useEffect, useState } from 'react';

function TournamentList() {
  const [tournaments, setTournaments] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    fetch('http://localhost:3001/api/tournaments')
      .then(res => res.json())
      .then(data => setTournaments(data))
      .catch(err => setError('Error al cargar torneos'));
  }, []);

  const handleEdit = (tournament) => {
    localStorage.setItem('editTournament', JSON.stringify(tournament));
    window.location.href = `/editar-torneo/${tournament.id}`;
  };

  const handleDelete = async (id) => {
    try {
      await fetch(`http://localhost:3001/api/tournaments/${id}`, {
        method: 'DELETE'
      });
      setTournaments(tournaments.filter(t => t.id !== id));
    } catch (err) {
      setError('Error al eliminar torneo');
    }
  };

  const handleCalendar = (id) => {
    window.location.href = `/torneo/${id}/calendar`;
  };


  return (
    <div style={{ padding: '2rem' }}>
      <h2>Torneos creados</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {tournaments.length === 0 ? (
        <p>No hay torneos registrados</p>
      ) : (
        <ul>
          {tournaments.map(t => (
            <li key={t.id}>
              <strong>{t.name}</strong> ‚Äî {t.startDate} a {t.endDate}<br />
              <em>{t.description}</em><br />
              <small>{t.rules}</small><br /><br />
              <button onClick={() => handleEdit(t)}>‚úèÔ∏è Update</button>
              <button onClick={() => handleDelete(t.id)} style={{ marginLeft: '1rem' }}>üóëÔ∏è Delete</button>
              <button onClick={() => handleCalendar(t.id)} style={{ marginLeft: '1rem' }}>üìÖ Calendar</button>
              <hr />
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default TournamentList;
