import React, { useEffect, useState } from 'react';
import { gapi } from 'gapi-script';

const CLIENT_ID = '591704407107-lh3mjl4c9t9co83akvrco6v7vlguq37s.apps.googleusercontent.com';
const API_KEY = 'AIzaSyDmvixL1GMLpRgba_hs1FMSSArB56LMjxc';
const SCOPES = 'https://www.googleapis.com/auth/calendar.events';

function GoogleCalendar() {
  const [events, setEvents] = useState([]);

  useEffect(() => {
    function start() {
      gapi.client.init({
        apiKey: API_KEY,
        clientId: CLIENT_ID,
        scope: SCOPES,
        discoveryDocs: ['https://www.googleapis.com/discovery/v1/apis/calendar/v3/rest']
      }).then(() => {
        gapi.auth2.getAuthInstance().signIn().then(() => {
          loadEvents();
        });
      });
    }

    gapi.load('client:auth2', start);
  }, []);

  const loadEvents = () => {
    gapi.client.calendar.events.list({
      calendarId: 'primary',
      timeMin: new Date().toISOString(),
      showDeleted: false,
      singleEvents: true,
      orderBy: 'startTime'
    }).then(response => {
      setEvents(response.result.items);
    });
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Eventos de Google Calendar</h2>
      <ul>
        {events.map(ev => (
          <li key={ev.id}>
            <strong>{ev.summary}</strong><br />
            {ev.start.dateTime || ev.start.date} â†’ {ev.end.dateTime || ev.end.date}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default GoogleCalendar;
