import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { gapi } from "gapi-script";

const CLIENT_ID =
  "591704407107-lh3mjl4c9t9co83akvrco6v7vlguq37s.apps.googleusercontent.com";
const API_KEY = "AIzaSyDmvixL1GMLpRgba_hs1FMSSArB56LMjxc";
const SCOPES = "https://www.googleapis.com/auth/calendar.events";

function Calendar() {
  const { id } = useParams(); // ID del torneo
  const [events, setEvents] = useState([]);
  const [error, setError] = useState("");
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
    if (!window.gapi) {
      setError("Google API no est√° disponible");
      return;
    }

    window.gapi.load("client:auth2", () => {
      window.gapi.client
        .init({
          apiKey: API_KEY,
          clientId: CLIENT_ID,
          scope: SCOPES,
          discoveryDocs: [
            "https://www.googleapis.com/discovery/v1/apis/calendar/v3/rest",
          ],
        })
        .then(() => {
          const authInstance = window.gapi.auth2.getAuthInstance();
          if (!authInstance.isSignedIn.get()) {
            authInstance
              .signIn()
              .then(() => {
                handleAuth();
              })
              .catch((err) => {
                setError("Error al iniciar sesi√≥n con Google");
                console.error(err);
              });
          } else {
            handleAuth();
          }
        })
        .catch((err) => {
          setError("Error al inicializar gapi");
          console.error(err);
        });
    });
  }, []);

  const handleAuth = () => {
    try {
      const googleUser = window.gapi.auth2.getAuthInstance().currentUser.get();
      const accessToken = googleUser.getAuthResponse().access_token;

      fetch("http://localhost:3000/set-auth", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ token: accessToken }),
      })
        .then((res) => {
          if (!res.ok) throw new Error("Error en set-auth");
          setIsAuthenticated(true);
          loadEvents();
        })
        .catch((err) => {
          setError("Error al conectar con el microservicio");
          console.error(err);
        });
    } catch (err) {
      setError("Error al obtener token de Google");
      console.error(err);
    }
  };

  const loadEvents = () => {
    fetch("http://localhost:3000/events")
      .then((res) => res.json())
      .then((data) => setEvents(data))
      .catch((err) => {
        setError("Error al cargar eventos");
        console.error(err);
      });
  };

  return (
    <div style={{ padding: "2rem" }}>
      <h2>Calendar del torneo</h2>
      {error && <p style={{ color: "red" }}>{error}</p>}
      {!isAuthenticated ? (
        <p>üîê Iniciando sesi√≥n con Google...</p>
      ) : (
        <ul>
          {events.map((ev) => (
            <li key={ev.id}>
              <strong>{ev.summary}</strong>
              <br />
              {ev.start.dateTime || ev.start.date} ‚Üí{" "}
              {ev.end.dateTime || ev.end.date}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default Calendar;
