import React, { useState } from 'react';

function CreateTournament() {
  const [form, setForm] = useState({
    name: '',
    description: '',
    startDate: '',
    endDate: '',
    maxTeams: '',
    rules: ''
  });

  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch('http://localhost:3001/api/tournaments', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(form)
      });
      const data = await res.json();
      setMessage(`Torneo creado: ${data.name}`);
      setForm({
        name: '',
        description: '',
        startDate: '',
        endDate: '',
        maxTeams: '',
        rules: ''
      });
    } catch (err) {
      setMessage('Error al crear torneo');
    }
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Crear nuevo torneo</h2>
      <form onSubmit={handleSubmit}>
        <input name="name" placeholder="Nombre" value={form.name} onChange={handleChange} required /><br /><br />
        <textarea name="description" placeholder="Descripción" value={form.description} onChange={handleChange} required /><br /><br />
        <input name="startDate" type="date" value={form.startDate} onChange={handleChange} required /><br /><br />
        <input name="endDate" type="date" value={form.endDate} onChange={handleChange} required /><br /><br />
        <input name="maxTeams" type="number" placeholder="Máximo de equipos" value={form.maxTeams} onChange={handleChange} required /><br /><br />
        <textarea name="rules" placeholder="Reglas del torneo" value={form.rules} onChange={handleChange} required /><br /><br />
        <button type="submit">Crear torneo</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
}

export default CreateTournament;
