import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

function TournamentCalendar() {
  const { id } = useParams();
  const [events, setEvents] = useState([]);
  const [error, setError] = useState('');
  const [form, setForm] = useState({
    summary: '',
    start: '',
    end: ''
  });

  useEffect(() => {
    fetch('http://localhost:3000/events') // microservicio de calendario
      .then(res => res.json())
      .then(data => setEvents(data))
      .catch(err => setError('Error al cargar eventos'));
  }, []);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleCreate = async (e) => {
    e.preventDefault();
    try {
      await fetch('http://localhost:3000/event', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(form)
      });
      setForm({ summary: '', start: '', end: '' });
      window.location.reload();
    } catch (err) {
      setError('Error al crear evento');
    }
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Eventos del torneo</h2>
      <form onSubmit={handleCreate}>
        <input name="summary" placeholder="Título del evento" value={form.summary} onChange={handleChange} required /><br /><br />
        <input name="start" type="datetime-local" value={form.start} onChange={handleChange} required /><br /><br />
        <input name="end" type="datetime-local" value={form.end} onChange={handleChange} required /><br /><br />
        <button type="submit">Crear evento</button>
      </form>
      <hr />
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <ul>
        {events.map(ev => (
          <li key={ev.id}>
            <strong>{ev.summary}</strong><br />
            {ev.start} → {ev.end}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default TournamentCalendar;
