import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

function EditTournament() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [form, setForm] = useState({
    name: '',
    description: '',
    startDate: '',
    endDate: '',
    maxTeams: '',
    rules: ''
  });

  useEffect(() => {
    const stored = localStorage.getItem('editTournament');
    if (stored) {
      setForm(JSON.parse(stored));
    }
  }, []);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      await fetch(`http://localhost:3001/api/tournaments/${id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(form)
      });
      navigate('/torneos');
    } catch (err) {
      alert('Error al actualizar torneo');
    }
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Editar torneo</h2>
      <form onSubmit={handleUpdate}>
        <input name="name" value={form.name} onChange={handleChange} required /><br /><br />
        <textarea name="description" value={form.description} onChange={handleChange} required /><br /><br />
        <input name="startDate" type="date" value={form.startDate} onChange={handleChange} required /><br /><br />
        <input name="endDate" type="date" value={form.endDate} onChange={handleChange} required /><br /><br />
        <input name="maxTeams" type="number" value={form.maxTeams} onChange={handleChange} required /><br /><br />
        <textarea name="rules" value={form.rules} onChange={handleChange} required /><br /><br />
        <button type="submit">Actualizar torneo</button>
      </form>
    </div>
  );
}

export default EditTournament;
